import React,{ useState} from "react";
import { NavLink } from "react-router-dom";

const NavBar = () => {
const [isDarkMode,setDarkMode] =useState(false)
const handleButton=()=>{
  setIsDark((isDarkMode) => !isDarkMode)
}
  return (
<header className="navcontainer">
  <div className="my-app">
  <h2><span className="title">Emergency</span> Application</h2>
  </div>
  <div className="links">
  <NavLink
  to="/:id/:username/:role"
  exact="true"
  >
    <li>Home</li>
  </NavLink>

  {/* <NavLink
  to="/register"
  exact="true"
  >
        <li>Register</li>
  </NavLink> */}

  <NavLink
  to="/reports"
  exact="true"
  >
    <li>Reports</li>
  </NavLink>

  <NavLink
  to="/"
  exact="true"
  >
   <li>Logout</li> 
  </NavLink>
  <button onClick={handleButton}>{isDarkMode ? "App dark" : "App light"} Mode</button>
  </div>

</header>
  );
};

export default NavBar;
