import React, { useState } from "react";


const Login = () => {
  // const [userName, setUserName] = useState("");
  // const [email, setEmail] = useState("");
  const [messages, setMessage] = useState("");
  const [submittedData, setSubmittedData] = useState({
    email: "",
    password: "",
  });

  const handleEmail = (event) => {
    setSubmittedData({
      ...submittedData,
      [event.target.name]: event.target.value,
    });
  };

  const handlePassword = (event) => {
    setSubmittedData({
      ...submittedData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const inputData = {
      email: submittedData.email,
      password: submittedData.password,
    };

    // console.log(submittedData);
    fetch("http://localhost:9292/userlogin", {
      method: "POST",
      headers: {
        "content-Type": "application/json",
      },
      body: JSON.stringify(inputData),
    })
      .then((res) => res.json())
      .then((data) => setMessage(data.message));
    // window.location.reload()
  };


  const handleClick = () => {
      window.push("/path/to/push");
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
      <h2> {messages}</h2>
        <input
          type="email"
          required
          name="email"
          placeholder="Enter your name"
          onChange={handleEmail}
          value={submittedData.email}
        />
        <br />
        <input
          type="password"
          required
          name="password"
          placeholder="Enter your name"
          onChange={handlePassword}
          value={submittedData.password}
        />
        <br />
        <button onClick={handleClick} type="button">Login</button>

        <button type="submit"><Link type="submit" to="/">Login</Link></button>
      </form>
    </div>
  );
};

export default Login;
