import React,{useState} from 'react'

const Register = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
  //   const [submittedData, setSubmittedData] = useState([]);

    function handleFirstNameChange(event) {
      setFirstName(event.target.value);
    }

    function handleLastNameChange(event) {
      setLastName(event.target.value);
      // console.log(event.target.value);

    }

    function handleSubmit(event) {
      event.preventDefault();
      console.log(firstName,lastName);

    }

    return (
      <div>
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="Enter your name" onChange={handleFirstNameChange} value={firstName} />
          <input type="text" placeholder="Enter your name" onChange={handleLastNameChange} value={lastName} />
          <input type="password" placeholder="Password" onChange={handleLastNameChange} value={lastName} />
          <input type="text" placeholder="Enter your name" onChange={handleLastNameChange} value={lastName} />
          <button type="submit">Submit</button>
        </form>
      </div>
    );
}

export default Register
