import React,{ useState} from "react";
import { useParams } from "react-router-dom";
import NavBar from "./NavBar";
// import Footer from "./Footer";
// import Reports from "./Reports";
import NewReportForm from "./NewReportForm";
import ReportList from "./ReportList";

const Home = ({ report }) => {
  const [isHide, setIsHide] = useState(false);
  function handleHideShow(){
  if(isHide){
    setIsHide('')
  }
  else{
    setIsHide(< NewReportForm params={params}/>)
  }}
  const reportdisplay = report.map((repo) => (
    <ReportList id={repo.id} repo={repo} />
  ));

  // let [visible, setButtonVisibility] = useState("");

  let params = useParams();

  return (
    <div>
      <NavBar />
        <div className="displayuserName">
          <h1 className="userdetails">Welcome {params.username}!!</h1>
        </div>
        <div className="addreportbtn">
        <button className="add-btn" onClick={handleHideShow}>Show New Report form</button>
        {true ? [isHide] : null}
      </div>

        {/* Reports */}
        <h3 className="reportsheader">Reports display</h3>

              <div className="displayreports">
                <table>
                  <tr>
                  <th>Time&Date</th>
                  <th>Title</th>
                  <th>Description</th>
                  <th>Location</th>
                  <th>Action</th>
                  </tr>
                {reportdisplay}

                </table>
              </div>
        {/* Reportscontainerends */}
    </div>
  );
};

export default Home;
