import React,{useState} from 'react'

const Register = () => {
    // const [userName, setUserName] = useState("");
    // const [email, setEmail] = useState("");
    // const [password, setPassword] = useState("");
    const [submittedData, setSubmittedData] = useState({
      username: "",
       email: "",
       password: "",
       select: " "
    });

    const handleFirstNameChange=(event) =>{
      setSubmittedData({
        ...submittedData, [event.target.name]: event.target.value,
      });
    }

    const handleEmail=(event) =>{
      setSubmittedData({
        ...submittedData, [event.target.name]: event.target.value,
      });
    }

    const handlePassword=(event) =>{
      setSubmittedData({
        ...submittedData, [event.target.name]: event.target.value,
      });
    }
    const handleSelectChange=(event) =>{
      setSubmittedData({
        ...submittedData, [event.target.name]: event.target.value,
      });
    }

    const  handleSubmit=(event)=> {
      event.preventDefault();
      console.log(submittedData);

    }

    return (
      <div>
        <form onSubmit={handleSubmit}>
          <input type="text" name="username" placeholder="Enter your name" onChange={handleFirstNameChange} value={submittedData.username} /><br/>
          <input type="email" name="email" placeholder="Enter your name" onChange={handleEmail} value={submittedData.email} /><br/>
          <input type="password" name="password"  placeholder="Enter your name" onChange={handlePassword} value={submittedData.password} /><br/>
          <select name="role" value={submittedData.select}  onChange={handleSelectChange}>
          <option value="admin">Admin</option>
          <option  value="user">User</option>        
          </select><br />
          <button type="submit">Submit</button>
        </form>
      </div>
    );
}

export default Register
