import React,{useState} from 'react'

const Register = () => {
    // const [userName, setUserName] = useState("");
    // const [email, setEmail] = useState("");
    // const [password, setPassword] = useState("");
    const [submittedData, setSubmittedData] = useState([]);

    function handleFirstNameChange(event) {
      setSubmittedData({
        ...submittedData, userName: event.target.value,
      });
    }

    function handleEmail(event) {
      setSubmittedData({
        ...submittedData, email: event.target.value,
      });
    }

    function handlePassword(event) {
      setSubmittedData({
        ...submittedData, password: event.target.value,
      });
    }

    function handleSubmit(event) {
      event.preventDefault();

    }

    return (
      <div>
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="Enter your name" onChange={handleFirstNameChange} value={submittedData.userName} />
          <input type="text" placeholder="Enter your name" onChange={handleEmail} value={submittedData.email} />
          <input type="text" placeholder="Enter your name" onChange={handlePassword} value={submittedData.password} />
          <button type="submit">Submit</button>
        </form>
      </div>
    );
}

export default Register
