import React from "react";
import { useParams } from "react-router-dom";
import NavBar from "./NavBar";
import Footer from "./Footer";
import Reports from "./Reports";
import NewReportForm from "./NewReportForm";
import ReportList from "./ReportList";

const Home = ({ report, onAddReport }) => {
  const reportdisplay = report.map((repo) => (
    <ReportList id={repo.id} repo={repo} />
  ));

  // let [visible, setButtonVisibility] = useState("");

  let params = useParams();

  return (
    <div>
      <NavBar />
        <div className="">
          <h1>Welcome {params.username}!!</h1>
        </div>
        {/* Reports */}
        <div className="">
          <div>
            <div className="">
              <div className="">
                <h3>Reports display</h3>
                {reportdisplay}
              </div>
              <div className="">
                <div>
                  <h3>Report an emergency</h3>
                </div>

                <button className="hideshow">Show/hide new Report form</button>
                {/* {true ? [isHide] : null} */}
                <NewReportForm  onAddReport={onAddReport} />
              </div>
            </div>
          </div>
        </div>
        {/* Reportscontainerends */}
    </div>
  );
};

export default Home;
