import React,{ useState} from "react";
import { useParams } from "react-router-dom";
import NavBar from "./NavBar";
// import Footer from "./Footer";
// import Reports from "./Reports";
import NewReportForm from "./NewReportForm";
import ReportList from "./ReportList";

const Home = ({ report,onAddReport }) => {

  const [isHide, setIsHide] = useState(false);
  const [btnText, setBtnText] = useState("report an emergency");

  const reportdisplay = report.map((repo) => (
    <ReportList id={repo.id} repo={repo} />
  ));

  // let [visible, setButtonVisibility] = useState("");

  let params = useParams();

  const togglePopUp = () => {

    if(isHide){
      setIsHide('')
      setBtnText("report an emergency")
    }
    else{
      setIsHide(<NewReportForm params={params} onAddReport={onAddReport }/>)
      setBtnText("close")
    }

  }

  return (
    <div>
      <NavBar />
      <div className="form_container">
       {true ? [isHide] : null}
      </div>

        <div className="displayuserName">
          <h1 className="userdetails">Welcome {params.username}!!</h1>
        </div>
        <div className="formcont">
        <div className="">
        <button className="add-btn" onClick={ togglePopUp} >{btnText}</button>

      </div>
        </div>

        <div>
          <div>
        <input type="text" className="serch" placeholder="search by title"/>
         </div>


        {/* Reports */}
        <h3 className="reportsheader">Reports display</h3>

              <div className="displayreports">
                <table>
                  <tr>
                  <th>Time&Date</th>
                  <th>Title</th>
                  <th>Description</th>
                  <th>Location</th>
                  <th>Action</th>
                  </tr>
                {reportdisplay}

                </table>
              </div>
        {/* Reportscontainerends */}
        </div>
    </div>
  );
};

export default Home;
