import React,{useState} from 'react'

const Register = () => {
    // const [userName, setUserName] = useState("");
    // const [email, setEmail] = useState("");
    // const [password, setPassword] = useState("");
    const [submittedData, setSubmittedData] = useState({
      username: "",
      email: "",
      password: "",
      role: ""
    });

    const handleFirstNameChange=(event) =>{
      setSubmittedData({
        ...submittedData,
         [event.target.name]: event.target.value,
      });
    }

    const handleEmail=(event) =>{
      setSubmittedData({
        ...submittedData,
       [event.target.name]: event.target.value,
      });
    }

    const handlePassword=(event) =>{
      setSubmittedData({
        ...submittedData,
        [event.target.name]: event.target.value,
      });
    }
    const handleSelectChange=(event) =>{
      setSubmittedData({
        ...submittedData,
         [event.target.name]: event.target.value,
      });
    }

    const  handleSubmit=(event)=> {
      event.preventDefault();
      const inputData = {
        username:submittedData.username,
        email:submittedData.email,
        password:submittedData.password,
        role:submittedData.role
      }


      // console.log(submittedData);
      fetch("http://localhost:9292/userregistration",{
        method: "POST",
        headers: {
          "content-Type": "application/json"
        },
        body: JSON.stringify(inputData)
      })
      .then(res=> res.json())
      .then(data=>console.log(data))
      setSubmittedData(" ")


    }

    return (
      <div>
        <form onSubmit={handleSubmit}>
          <input type="text" required name="username" placeholder="Enter your name" onChange={handleFirstNameChange} value={submittedData.username} /><br/>
          <input type="email" required name="email" placeholder="Enter your name" onChange={handleEmail} value={submittedData.email} /><br/>
          <input type="password" required name="password"  placeholder="Enter your name" onChange={handlePassword} value={submittedData.password} /><br/>
          <select name="role" required value={submittedData.role}  onChange={handleSelectChange}>
          <option value="admin">Admin</option>
          <option  value="user">User</option>        
          </select><br />
          <button type="submit">Submit</button>
        </form>
      </div>
    );
}

export default Register
