import React, { useState } from "react";
import { Link } from "react-router-dom";
import Home from "./Home";

const Login = () => {
  // const [userName, setUserName] = useState("");
  // const [email, setEmail] = useState("");
  const [userData, setUserData] = useState("");
  const [submittedData, setSubmittedData] = useState({
    email: "",
    password: "",
  });

  const handleEmail = (event) => {
    setSubmittedData({
      ...submittedData,
      [event.target.name]: event.target.value,
    });
  };

  const handlePassword = (event) => {
    setSubmittedData({
      ...submittedData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const inputData = {
      email: submittedData.email,
      password: submittedData.password,
    };

    // console.log(submittedData);
    fetch("http://localhost:9292/userlogin", {
      method: "POST",
      headers: {
        "content-Type": "application/json",
      },
      body: JSON.stringify(inputData),
    })
      .then((res) => res.json())
      .then((data) => setUserData(data));
    // window.location.reload()
    // window.localStorage.setMessage("token", data.message.token);
    //     history.push("/");
  };


  if(userData.message == "login success"){

    if(userData.user.role == "admin"){

      window.location="/"

      {<Home username={userData.user.username}/>}
    }


  }


  return (
    <div>
      <form onSubmit={handleSubmit}>
      <h2> {userData.message} </h2>

        <input
          type="email"
          required
          name="email"
          placeholder="Enter your name"
          onChange={handleEmail}
          value={submittedData.email}
        />
        <br />
        <input
          type="password"
          required
          name="password"
          placeholder="Enter your name"
          onChange={handlePassword}
          value={submittedData.password}
        />
        <br />
        {/* <button type="submit">Login</button> */}
        <button>Login</button>
      </form>
    </div>
  );
};

export default Login;
