import React from "react";
import NavBar from "./NavBar";
import ReportList from "./ReportList";

const Reports = ({ report }) => {
  const reportdisplay = report.map((repo) => (
    <ReportList
      key={repo.id}
      created_at={repo.created_at}
      title={repo.title}
      description={repo.description}
    />
  ));

  const [isHide, setIsHide] = useState(false);
  function handleHideShow(){
  if(isHide){
    setIsHide('')
  }
  else{
    setIsHide(<NewPoemForm />)
  }}
  return (
    <div>
      <NavBar />
      <div className="report-container">
        <div className="reportdislay">
          <h3>Reports display</h3>
          {reportdisplay}
        </div>
        <div className="fomreport">
          <div>
            <h3>Report an emergency</h3>
          </div>
          <div className="sidebar">
            <button onClick={handleHideShow}>Show/hide new poem form</button>
            {true ? [isHide] : null}
          </div>
          <form className="formR">
             <input type="text" name="report" placeholder="Enter Type of emergency report"value="" /><br/>
              <input type="text" name="report"  placeholder="Enter Type of emergency report" value="" /><br/>
              <button className="report-btn">Submit</button>
             </form>
        </div>
      </div>
    </div>
  );
};

export default Reports;
