import React,{useState} from 'react'

const Register = () => {
    const [firstName, setFirstName] = useState({
      name: "",
      email: "",
      password: ""
    });

  //   const [submittedData, setSubmittedData] = useState([]);

    function handleFirstNameChange(event) {
      setFirstName(event.target.value);
    }



    function handleSubmit(event) {
      event.preventDefault();
      console.log(firstName);

    }

    return (
      <div>
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="Enter your name" onChange={handleFirstNameChange} value={firstName.name} />
          <input type="email" placeholder="Enter your Email" onChange={handleFirstNameChange} value={firstName.email} />
          <input type="password" placeholder="Password" onChange={handleFirstNameChange} value={firstName.password} />
          <button type="submit">Submit</button>
        </form>
      </div>
    );
}

export default Register
