import React, { useState } from "react";
import { useParams } from "react-router-dom";
import NavBar from "./NavBar";
// import Footer from "./Footer";
// import Reports from "./Reports";
import NewReportForm from "./NewReportForm";
import ReportList from "./ReportList";


const Home = ({ report, onAddReport ,onReportDelete,repo}) => {
  const [isHide, setIsHide] = useState(false);
  const [btnText, setBtnText] = useState("report an emergency");

  const reportdisplay = report.map((repo) => (
    <ReportList 
    id={repo.id} 
    repo={repo} 
    onReportDelete={onReportDelete} />
  ));

  // let [visible, setButtonVisibility] = useState("");

  let params = useParams();

  const togglePopUp = () => {
    if (isHide) {
      setIsHide("");
      setBtnText("report an emergency");
    } else {
      setIsHide(<NewReportForm params={params} onAddReport={onAddReport} />);
      setBtnText("close");
    }
  };

  const searchByTitle=(e)=>{
    fetch(`http://localhost:9292/filterreports/${repo.title}`)
    .then(res=>res.json())
    .t
  }


  return (
    <div>
    <NavBar />
    <div className="container-reports">

    <div className="form_container">{true ? [isHide] : null}</div>

      <div className="displayuserName">
        <h1 className="userdetails">Welcome {params.username}!!</h1>
      </div>
      <div className="formcont">
        <div className="">
          <button className="add-btn" onClick={togglePopUp}>
            {btnText}
          </button>
        </div>
      </div>

        <div className="searchbar">
          <input type="text" className="search" onChange={searchByTitle} placeholder="search by title" />
          <h3 >Reports display</h3>

        </div>

        {/* Reports */}
        <div className="reportsheader">
        </div>

        <div className="displayreports">
          <table>
            <tr>
              <th>Time&Date</th>
              <th>Title</th>
              <th>Description</th>
              <th>Location</th>
              <th>Action</th>
            </tr>
            {reportdisplay}
          </table>
          </div>
          </div>
        {/* Reportscontainerends */}
      </div>
  );
};

export default Home;
